    output_file = "CR_%s_%s_%s_%s_%s_%s%s.%s"%(
        User_Id,
        Cus_Id,
        CIT_Date_From,
        CIT_Date_To,
        CIT_Status,
        Cur_Code,
        tail,
        Format
        )
    return_file=None
