def row_to_dict(row,structure):
    d = {}
    for name in structure:
        Field,Type,Precision,Map,Error = structure[name]
        value = getattr(row,Field)
        
        if Type == int:
            if Map is None:
                try:
                    value = int(value)
                except:
                    value = Error
            else:
                try:
                    value = Map.get(int(value),Error)
                except:
                    value = Error
        elif Type == float:
            try:
                if Precision is not None:
                    value = float(value)
                else:
                    value = round(float(value,Precision))
            except:
                value = Error
        elif Type == str:
            value=str(value)
        else:
            value = Error
        d.update({name:value})
    return d

