@main.route('/progress',methods=['GET','POST'])
def progress():
    print("enter progress ...")
    def generate():
        x = 0
        print("enter generate ... x=",x)
        
        while x <= 100:
            print("in loop x=",x)
            yield "data:%s\n\n"%x
            x = x + 10
            time.sleep(1.0)
        print("exit generate ... x=",x)
            
    print("exiting progress ...")
    return Response(
                generate(), 
                mimetype= 'text/event-stream'
                )
